// test_spi.c

#define TEST

#include "unity.h"
#include "spi.h"
#include <string.h>
#include <stdint.h>

extern spi_state_t g_spi_state;
extern spi_config_t g_spi_config;

// Test-only helpers (provided in source when TEST is defined)
spi_state_t test_spi_get_state(void);
spi_config_t test_spi_get_config(void);

// Utility: All valid combinations for tests
static const uint8_t valid_modes[] = {0, 1, 2, 3};
static const uint32_t valid_speeds[] = {1000000, 4000000, 8000000};

// Utility: A default-filled buffer for transfers
static uint8_t tx_test_buffer[4096];
static uint8_t rx_test_buffer[4096];

void setUp(void)
{
    // Reset driver state and config before each test.
    g_spi_state = SPI_STATE_UNINITIALIZED;
    g_spi_config.mode = 0xFF;
    g_spi_config.speed_hz = 0;

    // Also clear test buffers
    memset(tx_test_buffer, 0xA5, sizeof(tx_test_buffer));
    memset(rx_test_buffer, 0x00, sizeof(rx_test_buffer));
}

// ==============================
// 1. spi_init
// ==============================

void test_spi_init_success(void)
{
    int ret = spi_init();
    TEST_ASSERT_EQUAL_INT(SPI_SUCCESS, ret);

    // State updated
    TEST_ASSERT_EQUAL_INT(SPI_STATE_INITIALIZED, test_spi_get_state());
    // Config set to defaults
    spi_config_t cfg = test_spi_get_config();
    TEST_ASSERT_EQUAL_UINT8(0, cfg.mode);
    TEST_ASSERT_EQUAL_UINT32(1000000, cfg.speed_hz);
}

void test_spi_init_already_initialized(void)
{
    // First call: should succeed
    TEST_ASSERT_EQUAL_INT(SPI_SUCCESS, spi_init());
    // Change config/state so we can check it doesn't get reset
    g_spi_config.mode = 2;
    g_spi_config.speed_hz = 4000000;

    // Second call: should fail
    TEST_ASSERT_EQUAL_INT(SPI_ERROR_ALREADY_INITIALIZED, spi_init());

    // State unchanged
    TEST_ASSERT_EQUAL_INT(SPI_STATE_INITIALIZED, test_spi_get_state());
    spi_config_t cfg = test_spi_get_config();
    TEST_ASSERT_EQUAL_UINT8(2, cfg.mode);
    TEST_ASSERT_EQUAL_UINT32(4000000, cfg.speed_hz);
}

// ==============================
// 2. spi_set_config
// ==============================

void test_spi_set_config_success_all_modes_speeds(void)
{
    for (size_t i = 0; i < sizeof(valid_modes)/sizeof(valid_modes[0]); ++i) {
        for (size_t j = 0; j < sizeof(valid_speeds)/sizeof(valid_speeds[0]); ++j) {
            setUp();
            TEST_ASSERT_EQUAL_INT(SPI_SUCCESS, spi_init());

            spi_config_t user_cfg;
            user_cfg.mode = valid_modes[i];
            user_cfg.speed_hz = valid_speeds[j];
            int ret = spi_set_config(&user_cfg);
            TEST_ASSERT_EQUAL_INT(SPI_SUCCESS, ret);

            // Confirm internal config
            spi_config_t actual = test_spi_get_config();
            TEST_ASSERT_EQUAL_UINT8(user_cfg.mode, actual.mode);
            TEST_ASSERT_EQUAL_UINT32(user_cfg.speed_hz, actual.speed_hz);
        }
    }
}

