#define TEST
#include "unity.h"
#include "spi.h"
#include <string.h>
#include <stdint.h>

/* --- Expose internal state/config for testing as required by instructions --- */
extern spi_state_t g_spi_state;
extern spi_config_t g_spi_config;

/* --- Test-only helpers from spi.c header (implemented only under TEST) --- */
spi_state_t test_spi_get_state(void);
spi_config_t test_spi_get_config(void);

/* --- setUp(): Reset global SPI state/config before each test --- */
void setUp(void) {
    g_spi_state = SPI_STATE_UNINITIALIZED;
    g_spi_config.mode = 0xAA;        // Garbage value to catch missing init
    g_spi_config.speed_hz = 0xFFFFFFFF;
}

/* --- tearDown: not used --- */
void tearDown(void) {}

/* -------- spi_init tests -------- */
void test_spi_init_fresh_initialization(void) {
    int ret = spi_init();
    TEST_ASSERT_EQUAL_INT(SPI_SUCCESS, ret);
    spi_state_t state = test_spi_get_state();
    TEST_ASSERT_EQUAL_INT(SPI_STATE_INITIALIZED, state);
    spi_config_t cfg = test_spi_get_config();
    TEST_ASSERT_EQUAL_UINT8(0, cfg.mode);
    TEST_ASSERT_EQUAL_UINT32(1000000, cfg.speed_hz);
}

void test_spi_init_double_initialization(void) {
    int ret1 = spi_init();
    TEST_ASSERT_EQUAL_INT(SPI_SUCCESS, ret1);
    g_spi_config.mode = 2;
    g_spi_config.speed_hz = 8000000;
    int ret2 = spi_init();
    TEST_ASSERT_EQUAL_INT(SPI_ERROR_ALREADY_INITIALIZED, ret2);
    spi_state_t state = test_spi_get_state();
    TEST_ASSERT_EQUAL_INT(SPI_STATE_INITIALIZED, state);
    // Check that config is not overwritten to defaults
    spi_config_t cfg = test_spi_get_config();
    TEST_ASSERT_EQUAL_UINT8(2, cfg.mode);
    TEST_ASSERT_EQUAL_UINT32(8000000, cfg.speed_hz);
}

void test_spi_init_repeated_calls(void) {
    int i;
    TEST_ASSERT_EQUAL_INT(SPI_SUCCESS, spi_init());
    g_spi_config.mode = 1;
    g_spi_config.speed_hz = 4000000;
    for (i = 0; i < 5; ++i) {
        int ret = spi_init();
        TEST_ASSERT_EQUAL_INT(SPI_ERROR_ALREADY_INITIALIZED, ret);
        spi_config_t cfg = test_spi_get_config();
        TEST_ASSERT_EQUAL_UINT8(1, cfg.mode);
        TEST_ASSERT_EQUAL_UINT32(4000000, cfg.speed_hz);
    }
}

