void test_spi_transfer_busy_state_restored(void) {
    TEST_ASSERT_EQUAL_INT(SPI_SUCCESS, spi_init());
    uint8_t tx[2] = {1, 2};
    uint8_t rx[2] = {0, 0};
    // Set an invalid length, make sure state remains initialized
    int ret = spi_transfer(tx, rx, 0);
    TEST_ASSERT_EQUAL_INT(SPI_ERROR_INVALID_LENGTH, ret);
    TEST_ASSERT_EQUAL_INT(SPI_STATE_INITIALIZED, test_spi_get_state());

    // Now with valid transfer: during spi_transfer, state is BUSY, but after, should be INITIALIZED
    // The function does not provide a callback, but we can check before/after
    TEST_ASSERT_EQUAL_INT(SPI_SUCCESS, spi_transfer(tx, rx, 2));
    TEST_ASSERT_EQUAL_INT(SPI_STATE_INITIALIZED, test_spi_get_state());
}

void test_spi_transfer_no_buffer_overrun(void) {
    TEST_ASSERT_EQUAL_INT(SPI_SUCCESS, spi_init());
    uint8_t rx[8], canary[2]={0xCC,0xCC};
    memset(rx, 0, sizeof(rx));
    uint8_t tx[8];
    for (size_t i=0;i<8;++i) tx[i]=i*2;

    // Setup canaries after the output buffer to detect overwrite
    uint8_t* overrun_area = (uint8_t*)(&rx[8]);
    memcpy(overrun_area, canary, sizeof(canary));

    int ret = spi_transfer(tx, rx, 8);
    TEST_ASSERT_EQUAL_INT(SPI_SUCCESS, ret);
    for (size_t i=0;i<8;++i) TEST_ASSERT_EQUAL_UINT8(tx[i], rx[i]);
    // Check canaries unchanged
    TEST_ASSERT_EQUAL_UINT8(0xCC, overrun_area[0]);
    TEST_ASSERT_EQUAL_UINT8(0xCC, overrun_area[1]);
}

